type (

	//
	EVar struct {
		Action string
		ID     string
		// Adapter
	}
)

// Start
func (w EVar) Start(done chan<- bool, m *mist.Mist) {

	//
	fmt.Printf("EVar worker '%v' is working job '%v'...\n\n", w.ID, w.Action)

	//
	switch w.Action {

	//
	case "list":
		w.list(m)
	}

	// release...
	done <- true
}

